<?include 'classes/basicReport.php';class Module extends basicReport{    public function __construct(){        $file_name = $this->getClassName(__FILE__);        $this->modulo = $file_name;        $this->clase = $file_name;        $this->addControls();        $this->setCrud('only_edit');        $this->setFilter(array("fecha", "id"));        $this->report_name="Reporte De Inventario";        $this->report_table="productos";        $this->report_type="3";    }}  